// Title case normalization for all heading titles.
// Rules: Capitalize principal words; keep common short articles/conjunctions/prepositions
// (a, an, the, and, but, or, for, nor, on, at, to, from, by, of, in, with, over, as, is, into, up, down, off, out, per, vs, vs., via)
// unless they are the first or last word. Preserve existing internal punctuation (apostrophes, numbers, commas, hyphens).
// This is applied programmatically instead of manually editing ~1000 lines of literals.
(function normalizeHeadingTitles(data) {
  const minorWords = new Set([
    'a','an','the','and','but','or','for','nor','on','at','to','from','by','of','in','with','over','as','is','into','up','down','off','out','per','vs','vs.','via'
  ]);
  const needsCap = w => !minorWords.has(w);
  function titleCase(str){
    return str
      .split(/(\s+)/) // keep whitespace tokens
      .map(token => {
        if (/^\s+$/.test(token)) return token; // whitespace
        // Handle word token (may include punctuation). Separate leading/trailing punctuation.
        const match = token.match(/^(\W*)([\w'’\-]+)(\W*)$/);
        if (!match) return token.charAt(0).toUpperCase() + token.slice(1); // fallback
        const [, lead, core, trail] = match;
        // Split hyphenated segments individually
        const segments = core.split(/(-)/); // keep hyphens
        const processed = segments.map((seg, idx, arr) => {
          if (seg === '-') return seg;
            const lower = seg.toLowerCase();
            // Determine position among words (ignore pure hyphen tokens & whitespace). We'll do a simple pass outside.
            return lower.charAt(0).toUpperCase() + lower.slice(1);
        }).join('');
        return lead + processed + trail;
      })
      .join(' ')
      .replace(/\s+/g,' ') // collapse accidental double spaces
      .trim()
      .split(' ') // now adjust minor words (excluding first & last)
      .map((word, i, arr) => {
        const base = word.replace(/^[^A-Za-z0-9]+|[^A-Za-z0-9]+$/g,'');
        if (i !== 0 && i !== arr.length -1 && base) {
          const lowerBase = base.toLowerCase();
          if (minorWords.has(lowerBase)) {
            // replace only the base part with lowercase
            return word.replace(base, lowerBase);
          }
        }
        return word; // keep as is (already capitalized above)
      })
      .join(' ');
  }
  for (const book of data) {
    if (!book.headings) continue;
    for (const h of book.headings) {
      if (h.title) {
        h.title = titleCase(h.title);
      }
    }
  }
})(NTHeadingData);

